package com.qyc.service;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.stereotype.Service;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;@Service@FeignClient(value = "login-safety")public interface Varifcation {    @GetMapping("/sec/log/verifyToken/{token}")    public boolean verifyToken(@PathVariable("token") String token);}